import React from 'react';


class Home extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            cars_list: []

        }
    }

    componentDidMount(){
        /* 
        this.setState({
            cars_list: [
                {make: 'Nissan', model: 'Skyline GTST'},
                {make: 'BMW', model: 'M4'},
                {make: 'Mazda', model: 'Miata'}
            ]

        })

*/

        let options = {
            method: 'GET',
            mode: 'cors',
            headers: {
                'Content-Type': 'application/json'
            }
        }

        fetch('http://localhost:4000/cars', options)
            .then((res)=>{
                console.log('rached');
                if (res.status !== 200){
                    console.log('Error retrieving cars list from server: 200');
                }
                else{
                    return res.json();
                }

            })
            .then((data)=>{
                console.log(data);
                this.setState({cars_list: data});
            });




    }




    render(){





        const listElement = this.state.cars_list.map((car)=>{
            return(
                                <li className="list_item">
                                    <div className="make">{car.make}</div>
                                    <div className="model">{car.model}</div>
                                </li>
        )})




        return (
            <div>


                <div className="image">
                    <div className="top_wrapper">
                        <div className="number">{this.state.cars_list.length}</div>
                        <div className="title">Cars Remaining with Manual Transmission in the U.S.</div>
                    </div>
                </div>

                <ul className="list">

                        {listElement}

                </ul>


                <div className="footer">

                    Photo Source: 
                    <br/>
                    https://pixabay.com/users/stocksnap-894430/

                    <br/>
                    <br/>

                    Powered By:
                    <br/>   
                     BS4, Requests/HTML-Requests

                    <br/>
                    <br/>
                    See an Error?
                    <br/>
                    Send an email to savemanuals

                </div>



            </div> 
        )
    }

}

export default Home;
