import React from 'react';


class Home extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            cars_list: []

        }
    }

    componentDidMount(){

        this.setState({
            cars_list: [
                {make: 'Audi', model: 'A3'},
                {make: 'BMW', model: 'M4'},
                {make: 'Mazda', model: 'Miata'}
            ]

        })


        /* 
        let options = {
            method: 'GET',
            mode: 'cors',
            headers: {
                'Content-Type': 'application/json'
            }
        }

        fetch('http://localhost/4000/cars', options)
            .then((res)=>{
                if (res.status != 200){
                    console.log('Error retrieving cars list from server: 200');
                }
                else{
                    return res.json();
                }

            })
            .then((data)=>{
                this.setState(cars_list = data.cars);
            });



            */
    }




    render(){





        const listElement = this.state.cars_list.map((car)=>{
            return(
                                <li className="list_item">
                                    <div className="make">{car.make}</div>
                                    <div className="model">{car.model}</div>
                                </li>
        )})




        return (
            <div>

                <div className="image">
                    <h2 className="number">{this.state.cars_list.length}</h2>
                    <h2 className="title">Manual Transmission Cars Remaining in the U.S.</h2>
                </div>

                <ul className="list">
                <li className="list_item_wrapper">
                    {listElement}
                </li>
                </ul>

                <div className="footer">

                        Photo Source 

                    <br/>

                        https://pixabay.com/users/stocksnap-894430/

                    <br/>
                    <br/>

                        Data Fetched by BS4, Requests/HTML-Requests

                </div>



            </div> 
        )
    }

}

export default Home;
